<?phpclass WelcomeController extends BaseController {       /**     * User Model     * @var User     */    protected $user;    /**     * Inject the models.     * @param User $user     */    public function __construct(User $user)    {        parent::__construct();        $this->user = $user;    }    	/**	 * Returns all the blog posts.	 *	 * @return View	 */	public function getIndex()	{	$eventos = Evento::all();    $calendarEvent = array();    foreach ($eventos as $evento){        $fecha = new DateTime($evento->fecha_inicio);        $fechaFin = new DateTime($evento->fecha_fin);        do {            $calendarEvent[$fecha->format('Y-m-d')] = array(                                "url" => URL::to('eventos/' . $fecha->format('Y-m-d') . '/details'),            );            $fecha->add(new DateInterval('P1D'));        }while($fecha <= $fechaFin);    }    JavaScript::put([        'calendariodeeventos' => $calendarEvent,    ]);    $entradas = Entrada::all();    return View::make('site/welcome',compact('entradas'));  	}	}